import React, { useContext, useState } from 'react'
import { GlobalState } from '../../GlobalState'
import Cart from '../headers/icon/cart.svg'
import Menu from '../headers/icon/menu.svg'
import Close from '../headers/icon/close.svg'
import { Link } from 'react-router-dom'

function Header() {

    const state = useContext(GlobalState)
    const [isLogged, setIsLogged] = state.userAPI.isLogged
    const [isAdmin, setIsAdmin] = state.UserAPI.isLogged
    // Admin ---------------
    const adminRouter = () => {
        return (
            <>
                <li><Link to="/create_product">Create Product</Link></li>
                <li><Link to="/category">Categories</Link></li>
            </>
        )
    }

    const loggedRouter = () => {
        return (
            <>
                <li><Link to="/history">History</Link></li>
                <li><Link to="/">Logout</Link></li>
            </>
        )
    }

    return (
        <header>
            <div className='menu'>
                <img src={Menu} alt="" width="30" />
            </div>
            <div className='logo'>
                <h1>
                    <Link to="/">{isAdmin ? 'Admin' : 'Eldhahri Shop'} </Link>
                </h1>
            </div>

            <ul>
                <li><Link to="/">{isAdmin ? 'Products' : 'Shop'}</Link></li>

                {isAdmin && adminRouter}
                {
                    isLogged ? loggedRouter :
                        <li><Link to="/login">login & Register</Link ></li>
                }

                <li><Link to="/login">login & Register</Link></li>
                <li>
                    <img src={Close} alt="" width='30' className='menu' />
                </li>
            </ul>

            {
                isAdmin
            }

            <div className='cart-icon'>
                <span>0</span>
                <Link to="/cart">
                    <img src={Cart} alt="" width='30' />
                </Link>

            </div>

        </header>
    )
}

export default Header
